REPLACE INTO `{tablename}` (`id`, `catid`, `title`, `thumb`, `keywords`, `description`, `hits`, `uid`, `author`, `status`, `url`, `tableid`, `inputip`, `inputtime`, `updatetime`, `displayorder`) VALUES(191, 11, '手机客户端的不停更新有大意义吗', '', '手机客户端', '对于某些强迫症用户来说，更新APP的唯一目的就是为了消除这个数字。每个人都有手机，因此每个人都有自己的一套APP组合，自己作为APP生杀予夺的裁判，这并不稀奇。而我作为一个普通的手机使用屌丝，却总在好奇，将很...', 0, 1, 'admin', 9, '/news/index.php?c=show&id=191', 0, '1370864164', 1378025219, 1378025219, 0);
